/* MOCK */
class Arborist {
  // TODO: although no results are expected other than an Arborist instance,
  // it may be wise to have a way to demonstrate that options have been
  // received by the constructor.
  constructor (options = {}) {
  }

  lastReifyOpts = null

  reify(options) {
    this.lastReifyOpts = { ...options }
    return Promise.resolve()
  }

  get lastReifyOptions () {
    return this.lastReifyOpts
  }
}

module.exports = Arborist

