const calls = []

module.exports = options => {
  const opts = { ...options }
  opts.args = [ ...options.args ]
  calls.push(opts)
  return Promise.resolve()
}

module.exports.getCallList = () => calls
module.exports.purgeCallList = () => calls.splice(0)

