const knownRepos = {}

module.exports.setTestConfig = (data) => {
  for (const key in knownRepos)
    delete knownRepos[key]
  for (const key in data)
    knownRepos[key] = data[key] // not worth making a deep copy.
}

module.exports.revs = (repoUrl, opts) => {
console.log('utilGit: I want', repoUrl)
  return repoUrl in knownRepos ?
    Promise.resolve(knownRepos[repoUrl]) :
    Promise.reject(new Error('404: Unknown git repository'))
}
