const testConfig = {}

module.exports = pkgJsonPath => {
  const pkg = testConfig[pkgJsonPath]
  return pkg ?
    Promise.resolve(pkg) :
    Promise.reject(new Error(`Unrecognized path ${pkgJsonPath}`))
}

module.exports.setTestConfig = (data) => {
  for (let prop in testConfig) delete testConfig[prop]
  Object.assign(testConfig, data)
}
